package Practice;
import java.util.*;
public class FindingCharOfODDindex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner sc= new Scanner(System.in);
String r=sc.nextLine();
for(int i=0; i<r.length();i++) {
	char ch=r.charAt(i);
	if(i%2==0) {
		System.out.print(ch +" "); 
	
}

	}

	}
}
